//SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

contract MultiSigWallet {
    event Deposit(address indexed sender, uint amount, uint balance);
    event Transfer(
        address indexed sender,
        address indexed receiver,
        uint amount
    );
    event ConfirmTransaction(address indexed owner, uint indexed txIndex);
    event RevokeConfirmation(address indexed owner, uint indexed txIndex);
    event ExecuteTransaction(address indexed owner, uint indexed txIndex);
    event SubmitTransaction(
        address indexed owner,
        uint indexed txIndex,
        address indexed to,
        uint value,
        bytes data
    );
    event SetNumConfirmationsRequired(uint indexed numConfirmationsRequired);
    event Pause();
    event Unpause();
    event Recovery(address indexed oldAccount, address indexed newAccount);

    mapping(address => bool) public isOwner;
    mapping(uint256 => mapping(address => bool)) public isConfirmed;

    address[] public owners;
    Transaction[] public transactions;

    uint256 public numConfirmationsRequired;
    uint256 public emergencyWithdrawalTime;
    bool paused;

    struct Transaction {
        address payable to;
        uint value;
        bytes data;
        bool executed;
        uint256 numConfirmations;
    }

    modifier onlyOwner() {
        require(isOwner[msg.sender] = true, "Avaiable to Owners");
        _;
    }
    modifier txExists(uint256 _txIndex) {
        require(_txIndex < transactions.length, "Tx does not exist");
        _;
    }
    modifier notConfirmed(uint256 _txIndex) {
        require(!isConfirmed[_txIndex][msg.sender], "Tx already comfirmed");
        _;
    }
    modifier notExecuted(uint256 _txIndex) {
        require(!transactions[_txIndex].executed, "Tx already executed");
        _;
    }
    modifier whenNotPaused() {
        require(!paused, "Wallet is paused");
        _;
    }

    /**
     * constructor function is used to create a new instance of the contract and set the initial owners and number of required confirmations.
     *
     * @param _owners: An array of addresses representing the initial owners of the wallet
     * @param _numConfirmationsRequired: The number of required confirmations for a transaction to be executed
     */
    constructor(address[] memory _owners, uint256 _numConfirmationsRequired) {
        require(_owners.length > 0, "Invalid number of member");
        require(
            _numConfirmationsRequired > 0 &&
                _numConfirmationsRequired < _owners.length,
            "invalid number of required confirmations"
        );

        for (uint256 i; i < _owners.length; i++) {
            address owner = _owners[i];
            require(owner != address(0), "Invalid Address");
            require(!isOwner[owner], "owner is not unique");

            isOwner[owner] = true;
            owners.push(owner);
        }
        numConfirmationsRequired = _numConfirmationsRequired;
    }

    /**
     * addOwner: This function allows an existing owner to add a new owner to the wallet.
     *
     * @param _account: The address of the new owner
     *
     * Requirements
     *  The caller must be an existing owner of the wallet
     *  The new owner's address must not already be an owner of the wallet
     */
    function addOwner(address _account) external onlyOwner {
        require(!isOwner[_account], "Address already exist");
        require(_account != address(0), "Invalid Address");
        isOwner[_account] = true;
        owners.push(_account);
        // numConfirmations is incremented when new members are added.
        numConfirmationsRequired++;
    }

    /**
     * removeOwner: This function allows an existing owner to remove an existing owner from the wallet.
     *
     * @param _account: The address of the owner to remove
     *
     * Requirements
     *  The caller must be an existing owner of the wallet
     *  The owner to remove must be an existing owner of the wallet
     *  The wallet must have more than one owner
     */
    function removeOwner(address _account) external onlyOwner {
        bool findMember;
        uint256 indexedMember;
        require(owners.length > 1, "Cannot remove all owner of the wallet");
        for (uint256 i = 0; i < owners.length; i++) {
            if (_account == owners[i]) {
                findMember = true;
                indexedMember = i;
                break;
            }
        }
        require(findMember, "Not an owner of the wallet");
        isOwner[owners[indexedMember]] = false;
        owners[indexedMember] = owners[owners.length - 1];
        owners.pop();
        // numConfirmations is decremented when new members are removed.
        numConfirmationsRequired--;
    }

    /**
     * submitTransaction: This function allows an owner to submit a transaction to be executed by the wallet.
     *
     * @param _to: The address of the recipient of the transaction
     * @param _data: Additional data to include in the transaction
     *
     * Requirements
     *  The caller must be an owner of the wallet
     *  The wallet must not be paused
     */
    function submitTransaction(
        address payable _to,
        bytes memory _data
    ) public payable whenNotPaused onlyOwner {
        uint txIndex = (transactions.length - 1);
        require(msg.value > 0, "insufficient balance");

        transactions.push(
            Transaction({
                to: _to,
                value: msg.value,
                data: _data,
                executed: false,
                numConfirmations: 0
            })
        );

        emit SubmitTransaction(msg.sender, txIndex, _to, msg.value, _data);
    }

    /**
     * confirmTransaction: This function allows an owner to confirm a transaction.
     * @param _txIndex: The index of the transaction to confirm
     *
     * Requirements
     *  The caller must be an owner of the wallet
     *  The transaction must not already have been confirmed by the caller
     *  The transaction must not already have been executed.
     *  The transaction must exist
     *  The wallet must not be paused
     */
    function confirmTransaction(
        uint _txIndex
    )
        public
        onlyOwner
        txExists(_txIndex)
        notExecuted(_txIndex)
        notConfirmed(_txIndex)
    {
        Transaction storage transaction = transactions[_txIndex];
        transaction.numConfirmations += 1;
        isConfirmed[_txIndex][msg.sender] = true;

        emit ConfirmTransaction(msg.sender, _txIndex);
    }

    /**
     * executeTransaction: This function allows an owner to execute a confirmed transaction.
     * @param _txIndex: The index of the transaction to confirm
     *
     * Requirements
     *  The caller must be an owner of the wallet
     *  The transaction must not already have been executed
     *  The transaction must exist
     *  The wallet must not be paused
     *  The numConfirmations greater or equal to numConfirmationsRequired
     */
    function executeTransaction(
        uint _txIndex
    ) public onlyOwner txExists(_txIndex) whenNotPaused notExecuted(_txIndex) {
        Transaction storage transaction = transactions[_txIndex];

        require(
            transaction.numConfirmations >= numConfirmationsRequired,
            "cannot execute tx"
        );

        transaction.executed = true;

        (bool success, ) = transaction.to.call{value: transaction.value}(
            transaction.data
        );
        require(success, "tx failed");

        emit ExecuteTransaction(msg.sender, _txIndex);
        emit Transfer(msg.sender, transaction.to, transaction.value);
    }

    /**
     * revokeConfirmation: This function allows an owner to revoke
     *  their confirmation for a transaction.
     *  If a transaction does not have enough confirmations after one is revoked,
     *  it cannot be executed.
     *
     * @param _txIndex: The index of the transaction to confirm
     *
     * Requirements
     *  The caller must be an owner of the wallet
     *  The transaction must not already have been confirmed by the caller
     *  The transaction must not already have been executed.
     *  The transaction must exist
     */
    function revokeConfirmation(
        uint _txIndex
    ) public onlyOwner txExists(_txIndex) notExecuted(_txIndex) {
        Transaction storage transaction = transactions[_txIndex];

        require(isConfirmed[_txIndex][msg.sender], "tx not confirmed");

        transaction.numConfirmations -= 1;
        isConfirmed[_txIndex][msg.sender] = false;

        emit RevokeConfirmation(msg.sender, _txIndex);
    }

    // This function allows an owner to pause the wallet
    function pause() external onlyOwner {
        require(!paused, "Wallet is already paused");
        paused = true;
        emit Pause();
    }

    // This function allows an owner to unpause the wallet
    function unpause() external onlyOwner {
        require(paused, "Wallet is not paused");
        paused = false;
        emit Unpause();
    }

    function setEmergencyWithdrawalTime() public onlyOwner {
        emergencyWithdrawalTime = block.timestamp + 24 hours;
    }

    // The emergencyWithdrawal function in this smart contract allows an owner of the contract
    // to perform a withdrawal of all the funds stored in the contract in case of an emergency.

    // The user has to setEmergencyWithdrawalTime which will automatically
    // add 24 hours to the current block.timestamp,
    // then the owner has to wait for the time to elapse.
    function emergencyWithdrawal(address payable _to) external {
        require(
            isOwner[msg.sender],
            "Only an owner can perform an emergency withdrawal"
        );
        require(address(this).balance > 0, "Insufficient Balance");

        require(
            block.timestamp >= emergencyWithdrawalTime,
            "Emergency withdrawal period has not started"
        );
        _to.transfer(address(this).balance);
    }

    function setNumConfirmationsRequired(
        uint256 _numConfirmationsRequired
    ) external onlyOwner {
        require(
            _numConfirmationsRequired > 0 &&
                _numConfirmationsRequired <= owners.length,
            "Invalid number of required confirmations"
        );
        numConfirmationsRequired = _numConfirmationsRequired;

        emit SetNumConfirmationsRequired(_numConfirmationsRequired);
    }

    function changeOwner(
        address _oldOwner,
        address _newOwner
    ) external onlyOwner {
        // Check that the old owner is an existing owner of the wallet
        require(isOwner[_oldOwner], "Old owner is not an owner of the wallet");

        // Check that the new owner is not an existing owner of the wallet
        require(
            !isOwner[_newOwner],
            "New owner is already an owner of the wallet"
        );

        // Check that the new owner is not the zero address
        require(_newOwner != address(0), "Invalid new owner address");

        // Update the mapping to reflect the change in ownership
        isOwner[_oldOwner] = false;
        isOwner[_newOwner] = true;

        // Update the owners array to reflect the change in ownership
        for (uint256 i = 0; i < owners.length; i++) {
            if (owners[i] == _oldOwner) {
                owners[i] = _newOwner;
                break;
            }
        }

        emit Recovery(_oldOwner, _newOwner);
    }

    function totalSupply() external view returns (uint256) {
        return (address(this)).balance;
    }

    function getOwners() public view returns (address[] memory) {
        return owners;
    }

    function getTransactionCount() public view returns (uint) {
        return transactions.length;
    }

    function getTransaction(
        uint _txIndex
    )
        public
        view
        returns (
            address to,
            uint value,
            bytes memory data,
            bool executed,
            uint256 numConfirmations
        )
    {
        Transaction storage transaction = transactions[_txIndex];

        return (
            transaction.to,
            transaction.value,
            transaction.data,
            transaction.executed,
            transaction.numConfirmations
        );
    }

    receive() external payable {
        emit Deposit(msg.sender, msg.value, address(this).balance);
    }
}

contracts/InterestRateModel.sol.content:
// SPDX-License-Identifier: BSD-3-Clause
pragma solidity ^0.8.10;

/**
 * @title InterestRateModel Interface
 */
abstract contract InterestRateModel {
    /// @notice Indicator that this is an InterestRateModel contract (for inspection)
    bool public constant isInterestRateModel = true;

    /**
     * @notice Calculates the current borrow interest rate per block
     * @param cash The total amount of cash the market has
     * @param borrows The total amount of borrows the market has outstanding
     * @param reserves The total amount of reserves the market has
     * @return The borrow rate per block (as a percentage, and scaled by 1e18)
     */
    function getBorrowRate(
        uint cash,
        uint borrows,
        uint reserves
    ) external view virtual returns (uint);

    /**
     * @notice Calculates the current supply interest rate per block
     * @param cash The total amount of cash the market has
     * @param borrows The total amount of borrows the market has outstanding
     * @param reserves The total amount of reserves the market has
     * @param reserveFactorMantissa The current reserve factor the market has
     * @return The supply rate per block (as a percentage, and scaled by 1e18)
     */
    function getSupplyRate(
        uint cash,
        uint borrows,
        uint reserves,
        uint reserveFactorMantissa
    ) external view virtual returns (uint);
}

contracts/JumpRateModelV4.sol.content:
// SPDX-License-Identifier: BSD-3-Clause
pragma solidity ^0.8.10;

import "./InterestRateModel.sol";
import "./SafeMath.sol";
import "./Ownership/Ownable.sol";

/**
 * @title Compound's JumpRateModel Contract V3
 * @author Compound (modified by Dharma Labs)
 * @notice Version 2 modifies Version 1 by enabling updateable parameters.
 * @notice Version 3 includes Ownable and have updatable blocksPerYear.
 * @notice Version 4 moves blocksPerYear to the constructor.
 */
contract JumpRateModelV4 is InterestRateModel, Ownable {
    using SafeMath for uint256;

    event NewInterestParams(
        uint256 baseRatePerBlock,
        uint256 multiplierPerBlock,
        uint256 jumpMultiplierPerBlock,
        uint256 kink
    );

    /**
     * @notice The approximate number of blocks per year that is assumed by the interest rate model
     */
    uint256 public blocksPerYear;

    /**
     * @notice The multiplier of utilization rate that gives the slope of the interest rate
     */
    uint256 public multiplierPerBlock;

    /**
     * @notice The base interest rate which is the y-intercept when utilization rate is 0
     */
    uint256 public baseRatePerBlock;

    /**
     * @notice The multiplierPerBlock after hitting a specified utilization point
     */
    uint256 public jumpMultiplierPerBlock;

    /**
     * @notice The utilization point at which the jump multiplier is applied
     */
    uint256 public kink;

    /**
     * @notice A name for user-friendliness, e.g. WBTC
     */
    string public name;

    /**
     * @notice Construct an interest rate model
     * @param baseRatePerYear The approximate target base APR, as a mantissa (scaled by 1e18)
     * @param multiplierPerYear The rate of increase in interest rate wrt utilization (scaled by 1e18)
     * @param jumpMultiplierPerYear The multiplierPerBlock after hitting a specified utilization point
     * @param kink_ The utilization point at which the jump multiplier is applied
     * @param owner_ Sets the owner of the contract to someone other than msgSender
     * @param name_ User-friendly name for the new contract
     */
    constructor(
        uint256 blocksPerYear_,
        uint256 baseRatePerYear,
        uint256 multiplierPerYear,
        uint256 jumpMultiplierPerYear,
        uint256 kink_,
        address owner_,
        string memory name_
    ) {
        blocksPerYear = blocksPerYear_;
        name = name_;
        _transferOwnership(owner_);
        updateJumpRateModelInternal(
            baseRatePerYear,
            multiplierPerYear,
            jumpMultiplierPerYear,
            kink_
        );
    }

    /**
     * @notice Update the parameters of the interest rate model (only callable by owner, i.e. Timelock)
     * @param baseRatePerYear The approximate target base APR, as a mantissa (scaled by 1e18)
     * @param multiplierPerYear The rate of increase in interest rate wrt utilization (scaled by 1e18)
     * @param jumpMultiplierPerYear The multiplierPerBlock after hitting a specified utilization point
     * @param kink_ The utilization point at which the jump multiplier is applied
     */
    function updateJumpRateModel(
        uint256 baseRatePerYear,
        uint256 multiplierPerYear,
        uint256 jumpMultiplierPerYear,
        uint256 kink_
    ) external onlyOwner {
        updateJumpRateModelInternal(
            baseRatePerYear,
            multiplierPerYear,
            jumpMultiplierPerYear,
            kink_
        );
    }

    /**
     * @notice Calculates the utilization rate of the market: `borrows / (cash + borrows - reserves)`
     * @param cash The amount of cash in the market
     * @param borrows The amount of borrows in the market
     * @param reserves The amount of reserves in the market (currently unused)
     * @return The utilization rate as a mantissa between [0, 1e18]
     */
    function utilizationRate(
        uint256 cash,
        uint256 borrows,
        uint256 reserves
    ) public pure returns (uint256) {
        // Utilization rate is 0 when there are no borrows
        if (borrows == 0) {
            return 0;
        }

        return borrows.mul(1e18).div(cash.add(borrows).sub(reserves));
    }

    /**
     * @notice Updates the blocksPerYear in order to make interest calculations simpler
     * @param blocksPerYear_ The new estimated eth blocks per year.
     */
    function updateBlocksPerYear(uint256 blocksPerYear_) external onlyOwner {
        blocksPerYear = blocksPerYear_;
    }

    /**
     * @notice Calculates the current borrow rate per block, with the error code expected by the market
     * @param cash The amount of cash in the market
     * @param borrows The amount of borrows in the market
     * @param reserves The amount of reserves in the market
     * @return The borrow rate percentage per block as a mantissa (scaled by 1e18)
     */
    function getBorrowRate(
        uint256 cash,
        uint256 borrows,
        uint256 reserves
    ) public view override returns (uint256) {
        uint256 util = utilizationRate(cash, borrows, reserves);

        if (util <= kink) {
            return util.mul(multiplierPerBlock).div(1e18).add(baseRatePerBlock);
        } else {
            uint256 normalRate = kink.mul(multiplierPerBlock).div(1e18).add(
                baseRatePerBlock
            );
            uint256 excessUtil = util.sub(kink);
            return
                excessUtil.mul(jumpMultiplierPerBlock).div(1e18).add(
                    normalRate
                );
        }
    }

    /**
     * @notice Calculates the current supply rate per block
     * @param cash The amount of cash in the market
     * @param borrows The amount of borrows in the market
     * @param reserves The amount of reserves in the market
     * @param reserveFactorMantissa The current reserve factor for the market
     * @return The supply rate percentage per block as a mantissa (scaled by 1e18)
     */
    function getSupplyRate(
        uint256 cash,
        uint256 borrows,
        uint256 reserves,
        uint256 reserveFactorMantissa
    ) public view override returns (uint256) {
        uint256 oneMinusReserveFactor = uint256(1e18).sub(
            reserveFactorMantissa
        );
        uint256 borrowRate = getBorrowRate(cash, borrows, reserves);
        uint256 rateToPool = borrowRate.mul(oneMinusReserveFactor).div(1e18);
        return
            utilizationRate(cash, borrows, reserves).mul(rateToPool).div(1e18);
    }

    /**
     * @notice Internal function to update the parameters of the interest rate model
     * @param baseRatePerYear The approximate target base APR, as a mantissa (scaled by 1e18)
     * @param multiplierPerYear The rate of increase in interest rate wrt utilization (scaled by 1e18)
     * @param jumpMultiplierPerYear The multiplierPerBlock after hitting a specified utilization point
     * @param kink_ The utilization point at which the jump multiplier is applied
     */
    function updateJumpRateModelInternal(
        uint256 baseRatePerYear,
        uint256 multiplierPerYear,
        uint256 jumpMultiplierPerYear,
        uint256 kink_
    ) internal {
        baseRatePerBlock = baseRatePerYear.div(blocksPerYear);
        multiplierPerBlock = (multiplierPerYear.mul(1e18)).div(
            blocksPerYear.mul(kink_)
        );
        jumpMultiplierPerBlock = jumpMultiplierPerYear.div(blocksPerYear);
        kink = kink_;

        emit NewInterestParams(
            baseRatePerBlock,
            multiplierPerBlock,
            jumpMultiplierPerBlock,
            kink
        );
    }
}

contracts/Lens/BasicLens.sol.content:
// SPDX-License-Identifier: BSD-3-Clause
pragma solidity ^0.8.10;

import "../EIP20Interface.sol";
import "../PriceOracle.sol";

import "../RewardDistributor.sol";
import "../CToken.sol";
import "../Comptroller.sol";

import "hardhat/console.sol";

interface ComptrollerLensInterface {
    function claimComp(address) external;

    function rewardDistributor() external view returns (address);
}

contract BasicLens {
    constructor() {
        address rds = 0x9dD10b2269709F7369dfBF7bbAe2AD3aA53b16C7;
        address sbp = 0x2F471205AC647Dd11d3de04dB3C9A7FEDBAA66d6;
        address user = 0x5B78ace197872A4C90bb137D0643aA3755DBC1A0;
        // const
        uint256 beforeBalances = EIP20Interface(sbp).balanceOf(user);
        uint256 rdsBal = EIP20Interface(sbp).balanceOf(rds);
        console.log("beforeBalances: %s", beforeBalances);
        console.log("rdsBal: %s", rdsBal);

        address[] memory holders = new address[](1);
        holders[0] = user;
        uint256 tmp = rewardsAccruedSimulate(
            RewardDistributor(rds),
            user,
            holders,
            sbp
        );
        console.log("tmp: %s", tmp);
        RewardDistributor(rds).claim(holders);
        uint256 afterBalances = EIP20Interface(sbp).balanceOf(user);
        console.log("afterBalances: %s", afterBalances);
    }

    function rewardsAccruedSimulate(
        RewardDistributor rewardDistributor,
        address account,
        address[] memory holders,
        address rewardToken
    ) public returns (uint256) {
        uint256 beforeBalances = EIP20Interface(rewardToken).balanceOf(account);

        rewardDistributor.claim(holders);

        uint256 afterBalances = EIP20Interface(rewardToken).balanceOf(account);

        return afterBalances - beforeBalances;
    }

    function rewardsAccrued(
        ComptrollerLensInterface comptroller,
        address account
    )
        external
        returns (address[] memory rewardTokens, uint256[] memory accrued)
    {
        address externalRewardDistributor = comptroller.rewardDistributor();

        rewardTokens = RewardDistributor(externalRewardDistributor)
            .getRewardTokens();

        uint256[] memory beforeBalances = getBalancesInternal(
            rewardTokens,
            account
        );

        comptroller.claimComp(account);

        uint256[] memory afterBalances = getBalancesInternal(
            rewardTokens,
            account
        );

        accrued = new uint256[](rewardTokens.length);
        for (uint256 i = 0; i < rewardTokens.length; i++) {
            accrued[i] = afterBalances[i] - beforeBalances[i];
        }
    }

    function getBalancesInternal(
        address[] memory tokens,
        address account
    ) internal view returns (uint256[] memory balances) {
        balances = new uint256[](tokens.length);
        for (uint256 i = 0; i < tokens.length; i++) {
            balances[i] = EIP20Interface(tokens[i]).balanceOf(account);
        }
    }
}

contracts/Lens/ProtocolLens.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity =0.8.16;

import "./BasicLens.sol";

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "../CErc20.sol";
import "../JumpRateModelV4.sol";

contract ProtocolLens is BasicLens {
    string public constant mainSymbol = "ETH";
    string public constant mainCTokenSymbol = "sbETH";

    struct CTokenInfo {
        address underlyingAssetAddress;
        uint256 underlyingDecimals;
        address contractAddress;
        uint256 poolBalance;
        string underlyingSymbol;
        string symbol;
        uint8 decimals;
        bool isNative;
        uint256 supplyRatePerBlock;
        uint256 borrowRatePerBlock;
        uint256 totalSupply;
        uint256 totalBorrows;
        uint256 collateralFactor;
        uint256 oraclePrice;
        uint256 totalReserves;
        uint256 cash;
        uint256 reserveFactorMantissa;
        uint256 exchangeRateCurrent;
        uint256 multiplierPerBlock;
        uint256 kink;
        uint256 baseRatePerBlock;
        uint256 jumpMultiplierPerBlock;
        bool isMintPaused;
        bool isBorrowPaused;
        bool isSeizePaused;
        bool isTransferPaused;
        uint256 borrowCap;
        uint256 supplyCap;
    }

    // returns CTokenInfo
    // call using callStatic to avoid gas fee
    function getCTokenInfo(
        CToken cToken,
        Comptroller comptroller
    ) public returns (CTokenInfo memory) {
        address underlyingAssetAddress;
        uint256 underlyingDecimals;
        bool isNative = compareStrings(cToken.symbol(), mainCTokenSymbol);
        CTokenInfo memory cTokenInfo;

        if (isNative) {
            underlyingAssetAddress = address(0);
            underlyingDecimals = 18;
            cTokenInfo.underlyingSymbol = mainSymbol;
        } else {
            CErc20 cErc20 = CErc20(address(cToken));
            underlyingAssetAddress = cErc20.underlying();
            underlyingDecimals = EIP20Interface(cErc20.underlying()).decimals();
            cTokenInfo.underlyingSymbol = EIP20Interface(cErc20.underlying())
                .symbol();
        }

        cTokenInfo.isNative = isNative;

        address contractAddress = address(cToken);

        cTokenInfo.underlyingAssetAddress = underlyingAssetAddress;
        cTokenInfo.underlyingDecimals = underlyingDecimals;
        cTokenInfo.contractAddress = contractAddress;
        cTokenInfo.poolBalance = isNative
            ? contractAddress.balance
            : IERC20(underlyingAssetAddress).balanceOf(contractAddress);
        cTokenInfo.symbol = cToken.symbol();
        cTokenInfo.decimals = cToken.decimals();
        cTokenInfo.supplyRatePerBlock = cToken.supplyRatePerBlock();
        cTokenInfo.borrowRatePerBlock = cToken.borrowRatePerBlock();
        cTokenInfo.totalSupply = cToken.totalSupply();
        cTokenInfo.totalBorrows = cToken.totalBorrows();
        (, cTokenInfo.collateralFactor, ) = comptroller.markets(
            contractAddress
        );

        cTokenInfo.oraclePrice = getOraclePrice(cToken, comptroller);

        cTokenInfo.totalReserves = cToken.totalReserves();
        cTokenInfo.cash = cToken.getCash();
        cTokenInfo.reserveFactorMantissa = cToken.reserveFactorMantissa();
        cTokenInfo.exchangeRateCurrent = cToken.exchangeRateCurrent();

        JumpRateModelV4 interestRateModel = JumpRateModelV4(
            address(cToken.interestRateModel())
        );

        cTokenInfo.multiplierPerBlock = interestRateModel.multiplierPerBlock();
        cTokenInfo.kink = interestRateModel.kink();
        cTokenInfo.baseRatePerBlock = interestRateModel.baseRatePerBlock();
        cTokenInfo.jumpMultiplierPerBlock = interestRateModel
            .jumpMultiplierPerBlock();

        cTokenInfo.isMintPaused = comptroller.mintGuardianPaused(
            contractAddress
        );
        cTokenInfo.isBorrowPaused = comptroller.borrowGuardianPaused(
            contractAddress
        );
        cTokenInfo.isSeizePaused = comptroller.seizeGuardianPaused();
        cTokenInfo.isTransferPaused = comptroller.transferGuardianPaused();

        cTokenInfo.borrowCap = comptroller.borrowCaps(contractAddress);
        cTokenInfo.supplyCap = comptroller.supplyCaps(contractAddress);
        return cTokenInfo;
    }

    struct AccountInfo {
        uint256 mySuppliedBalance;
        uint256 myBorrowedBalance;
        uint256 myCTokenBalance;
        // uint256 mySupplyPrincipalBalance;
        // uint256 myBorrowPrincipalBalance;
        uint256 myRealTokenBalance;
        // uint256 incentiveTokenSupplierIndex;
        // uint256 incentiveTokenBorrowerIndex;
        uint256 allowance;
    }

    // returns AccountInfo
    function getAccountInfo(
        CToken cToken,
        address payable account
    ) public returns (AccountInfo memory) {
        AccountInfo memory accountInfo;

        address underlyingAssetAddress;
        address contractAddress = address(cToken);

        bool isNative = compareStrings(cToken.symbol(), mainCTokenSymbol);
        if (isNative) {
            underlyingAssetAddress = address(0);
            accountInfo.allowance = type(uint256).max;
        } else {
            CErc20 cErc20 = CErc20(address(cToken));
            underlyingAssetAddress = cErc20.underlying();
            accountInfo.allowance = EIP20Interface(underlyingAssetAddress)
                .allowance(account, contractAddress);
        }

        accountInfo.mySuppliedBalance = cToken.balanceOfUnderlying(account);
        accountInfo.myBorrowedBalance = cToken.borrowBalanceCurrent(account);
        accountInfo.myCTokenBalance = cToken.balanceOf(account);
        // accountInfo.mySupplyPrincipalBalance = cToken.supplyPrincipal(account);
        // accountInfo.myBorrowPrincipalBalance = cToken.borrowPrincipal(account);
        accountInfo.myRealTokenBalance = isNative
            ? account.balance
            : IERC20(underlyingAssetAddress).balanceOf(account);
        // accountInfo.incentiveTokenSupplierIndex = controller
        //     .incentiveTokenSupplierIndex(contractAddress, account);
        // accountInfo.incentiveTokenBorrowerIndex = controller
        //     .incentiveTokenBorrowerIndex(contractAddress, account);

        return accountInfo;
    }

    // get cToken's underlying price
    function getOraclePrice(
        CToken cToken,
        Comptroller comptroller
    ) public view returns (uint256) {
        PriceOracle oracle = comptroller.oracle();

        return oracle.getPrice(cToken);
    }

    struct CTokenMetaData {
        CTokenInfo cTokenInfo;
        AccountInfo accountInfo;
    }

    function cTokenMetaDataList(
        Comptroller comptroller
    ) external returns (CTokenMetaData[] memory) {
        CToken[] memory allMarkets = comptroller.getAllMarkets();
        CTokenMetaData[] memory result = new CTokenMetaData[](
            allMarkets.length
        );

        for (uint256 i = 0; i < allMarkets.length; i++) {
            result[i].cTokenInfo = getCTokenInfo(allMarkets[i], comptroller);
        }
        return result;
    }

    function cTokenMetaDataListAuth(
        address payable account,
        Comptroller comptroller
    ) external returns (CTokenMetaData[] memory) {
        CToken[] memory allMarkets = comptroller.getAllMarkets();
        CTokenMetaData[] memory result = new CTokenMetaData[](
            allMarkets.length
        );

        for (uint256 i = 0; i < allMarkets.length; i++) {
            result[i].cTokenInfo = getCTokenInfo(allMarkets[i], comptroller);
            result[i].accountInfo = getAccountInfo(allMarkets[i], account);
        }
        return result;
    }

    /* internal functions  */
    function compareStrings(
        string memory a,
        string memory b
    ) internal pure returns (bool) {
        return (keccak256(abi.encodePacked((a))) ==
            keccak256(abi.encodePacked((b))));
    }
}

contracts/Liquidator/IWeswapRouter.sol.content:
