// SPDX-License-Identifier: MIT

pragma solidity 0.8.16;

import {CEther} from "../CEther.sol";
import {IERC20} from "../Swap/IERC20.sol";

abstract contract Liquidator {
    address payable public cether;

    address public owner;

    constructor(address payable _cether) {
        cether = _cether;
        owner = msg.sender;
    }

    function swapToBorrowingAsset(address from, address to) public virtual {}

    function liquidate(
        address borrower,
        uint256 repayAmount,
        address cTokenCollateral
    ) external payable virtual;

    function claim(address token) external {
        require(msg.sender == owner, "only owner can claim");
        _claim(token);
    }

    function _claim(address token) internal {
        if (token == address(0)) {
            (bool suc, ) = payable(owner).call{value: address(this).balance}(
                ""
            );
            if (!suc) {
                CEther(cether).mint{value: address(this).balance}();
            }
        } else {
            uint256 bal = IERC20(token).balanceOf(address(this));
            require(token.code.length > 0, "SafeERC20: invalid token");
            (bool success, bytes memory data) = token.call(
                abi.encodeWithSelector(IERC20.transfer.selector, owner, bal)
            );
            require(
                success && (data.length == 0 || abi.decode(data, (bool))),
                "SafeERC20: transfer failed"
            );
        }
    }

    receive() external payable {}
}

