// SPDX-License-Identifier: MIT

pragma solidity 0.8.16;

import {Liquidator} from "./Liquidator.sol";
import {IWeswapRouter} from "./IWeswapRouter.sol";
import {IERC20} from "../Swap/IERC20.sol";

import "hardhat/console.sol";

interface IRedeem {
    function redeem(uint256 redeemTokens) external returns (uint256);
}

interface ICether {
    function liquidateBorrow(
        address borrower,
        address cTokenCollateral
    ) external payable;
}

contract LiquidatorWemix is Liquidator {
    IWeswapRouter public router =
        IWeswapRouter(0x80a5A916FB355A8758f0a3e47891dc288DAC2665);

    address public weth = 0x7D72b22a74A216Af4a002a1095C8C707d6eC1C5f;
    address public stwemix = 0x9B377bd7Db130E8bD2f3641E0E161cB613DA93De;

    constructor(address payable _cether) Liquidator(_cether) {
        IERC20(stwemix).approve(address(router), type(uint256).max);
    }

    function swapToBorrowingAsset(address from, address to) public override {
        if (from != address(0)) {
            uint256 bal = IERC20(from).balanceOf(address(this));
            if (bal > 0) {
                address[] memory path = new address[](2);
                path[0] = from;
                path[1] = to;

                console.log("try swap from", from);
                console.log("to", to);
                console.log("amount", bal);

                router.swapExactTokensForWEMIX(
                    bal,
                    1,
                    path,
                    address(this),
                    block.timestamp + 100
                );
            }
        }
    }

    function unwrap(address collateralCToken) internal {
        uint256 bal = IERC20(collateralCToken).balanceOf(address(this));
        if (bal > 0) {
            IRedeem(collateralCToken).redeem(bal);
        }
    }

    function liquidate(
        address borrower,
        uint256 repayAmount,
        address cTokenCollateral
    ) external payable override {
        swapToBorrowingAsset(stwemix, weth);

        console.log("balance", address(this).balance);

        uint256 inputBal = address(this).balance > repayAmount
            ? repayAmount
            : address(this).balance;
        console.log("try to liquidate", inputBal);
        ICether(cether).liquidateBorrow{value: inputBal}(
            borrower,
            cTokenCollateral
        );

        console.log("left value", address(this).balance);
        console.log(
            "left cToken",
            IERC20(cTokenCollateral).balanceOf(address(this))
        );

        unwrap(cTokenCollateral);

        console.log("left stwemix", IERC20(stwemix).balanceOf(address(this)));

        swapToBorrowingAsset(stwemix, weth);

        console.log("last value", address(this).balance);
        console.log(owner);
        _claim(address(0));

        console.log("after claim, left value", address(this).balance);
    }
}

