// SPDX-License-Identifier: MIT

pragma solidity ^0.8.16;

import "./InterfacesLockdrop.sol";

contract ReferralPool {
    address public dev;
    address public factory;

    struct ReferralInfo {
        address referrer;
        uint256 referralRewardStored;
        uint256 totalRefereeLockAmount;
        uint256 totalRefereeCount;
        address[] referees;
    }

    uint256 public devPoolStored;
    uint256 public referralRatio = 5000; // 50%
    mapping(address => ReferralInfo) public referralInfo;

    bool public claimPaused = true;

    modifier onlyFactory() {
        require(msg.sender == factory, "only factory");
        _;
    }

    constructor(address _dev) {
        dev = _dev;
        factory = msg.sender;
    }

    function registerReferral(
        address _user,
        address _referrer,
        uint256 _lockAmount
    ) external onlyFactory {
        ReferralInfo storage user = referralInfo[_user];

        // already registered  or no referrer
        if (user.referrer != address(0) || _referrer == address(0)) {
            return;
        }

        user.referrer = _referrer;

        referralInfo[_referrer].totalRefereeCount += 1;
        referralInfo[_referrer].referees.push(_user);

        referralInfo[_referrer].totalRefereeLockAmount += _lockAmount;
    }

    function commitReferralReward(
        address _user,
        uint256 _lockAmount
    ) external onlyFactory {
        if (_user == address(0)) {
            devPoolStored += _lockAmount;
        } else {
            ReferralInfo storage user = referralInfo[_user];

            if (user.referrer == address(0)) {
                devPoolStored += _lockAmount;
            } else {
                ReferralInfo storage referrer = referralInfo[user.referrer];
                referrer.referralRewardStored += _lockAmount;
            }
        }
    }

    function claimReferralReward() external {
        require(!claimPaused, "claim paused");
        ReferralInfo storage user = referralInfo[msg.sender];

        uint256 amount = user.referralRewardStored;
        user.referralRewardStored = 0;

        if (amount > 0) {
            uint256 referralReward = (amount * referralRatio) / 10000;
            uint256 devReward = amount - referralReward;

            if (referralReward > 0) {
                IWemixLockdropFactory(factory).CWemix().transfer(
                    user.referrer,
                    referralReward
                );
            }

            if (devReward > 0) {
                IWemixLockdropFactory(factory).CWemix().transfer(
                    dev,
                    devReward
                );
            }
        }
    }

    function claimDevPool() external {
        require(!claimPaused, "claim paused");
        require(msg.sender == dev, "only dev");

        uint256 amount = devPoolStored;
        devPoolStored = 0;

        if (amount > 0) {
            IWemixLockdropFactory(factory).CWemix().transfer(dev, amount);
        }
    }

    function setClaimPaused(bool _claimPaused) external {
        require(msg.sender == dev, "only dev");
        claimPaused = _claimPaused;
    }

    function setReferralRatio(uint256 _referralRatio) external {
        require(msg.sender == dev, "only dev");
        referralRatio = _referralRatio;
    }

    function setDev(address _dev) external {
        require(msg.sender == dev, "only dev");
        dev = _dev;
    }
}

