// SPDX-License-Identifier: MIT

pragma solidity ^0.8.16;

import "./InterfacesLockdrop.sol";

contract WemixLockdrop {
    IWemixLockdropFactory public factory;

    address public immutable maker;

    uint256 public depositAmount;

    constructor(address _maker) {
        factory = IWemixLockdropFactory(msg.sender);
        maker = _maker;
    }

    modifier onlyFactory() {
        require(msg.sender == address(factory), "only factory");
        _;
    }

    function lock() external payable onlyFactory {
        IWemixMint cWemix = factory.CWemix();

        cWemix.mint{value: msg.value}();

        depositAmount += msg.value;
    }

    function unlock(uint256 _amount) external onlyFactory {
        IWemixMint cWemix = factory.CWemix();
        cWemix.redeemUnderlying(_amount);

        (bool suc, ) = maker.call{value: _amount}("");
        require(suc, "transfer failed");

        uint256 cYield = cWemix.balanceOf(address(this));

        cWemix.transfer(factory.referralPool(), cYield);
        factory.commitReferralReward(maker, cYield);
    }

    function claimComptrollerReward(address _comptroller) external {
        require(
            msg.sender == address(factory) || msg.sender == maker,
            "invalid sender"
        );

        IComptrollerClaim(_comptroller).claimComp(address(this));

        address sbp = factory.shoebillPoint();
        IERC20(sbp).transfer(maker, IERC20(sbp).balanceOf(address(this)));
    }

    function recoverToken(address _token, uint256 _amount) external {
        require(msg.sender == factory.dev(), "invalid sender");

        if (_token == address(0)) {
            (bool suc, ) = factory.dev().call{value: _amount}("");
            require(suc, "transfer failed");
        } else {
            IERC20(_token).transfer(factory.dev(), _amount);
        }
    }

    receive() external payable {}
}

