// SPDX-License-Identifier: MIT

pragma solidity ^0.8.16;

import {Initializable} from "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "./WemixLockdrop.sol";
import "./ReferralPool.sol";

contract WemixLockdropFactory is Initializable {
    IWemixMint public CWemix;

    uint256 public lockdropStartTime;
    uint256 public lockdropFinishTime;

    uint256 public totalDeposit;
    uint256 public totalDepositCap;
    uint256 public individualDepositCap;

    address public dev;

    uint256 public lockState;

    address public referralPool;
    address public shoebillPoint;

    struct UserInfo {
        WemixLockdrop lockdropInstance;
        uint256 depositAmount;
    }

    mapping(address => UserInfo) public userInfo;

    constructor() {
        _disableInitializers();
    }

    modifier lock() {
        require(lockState == 1, "lock is not locked");
        lockState = 2;
        _;
        lockState = 1;
    }

    // token Reward => lock user
    // lockdrop reward => lock user
    // supply apy => distribute between refferer and lock user
    function initialize(
        IWemixMint _cWemix,
        uint256 _lockdropStartTime,
        uint256 _lockdropFinishTime,
        uint256 _totalDepositCap,
        uint256 _individualDepositCap,
        address _shoebillPoint
    ) external initializer {
        CWemix = _cWemix;
        lockdropStartTime = _lockdropStartTime;
        lockdropFinishTime = _lockdropFinishTime;

        totalDepositCap = _totalDepositCap;
        individualDepositCap = _individualDepositCap;

        referralPool = address(new ReferralPool(msg.sender));
        shoebillPoint = _shoebillPoint;

        dev = msg.sender;

        lockState = 1;
    }

    function deposit(address _referrer) external payable lock {
        require(
            block.timestamp >= lockdropStartTime,
            "lockdrop is not started"
        );
        require(block.timestamp < lockdropFinishTime, "lockdrop is finished");
        require(msg.value > 0, "invalid amount");
        require(_referrer != msg.sender, "self referrer");

        UserInfo storage user = userInfo[msg.sender];

        require(
            user.depositAmount + msg.value <= individualDepositCap,
            "exceed individual deposit cap"
        );
        require(
            totalDeposit + msg.value <= totalDepositCap,
            "exceed total deposit cap"
        );

        ReferralPool(referralPool).registerReferral(
            msg.sender,
            _referrer,
            msg.value
        );

        if (address(user.lockdropInstance) == address(0)) {
            WemixLockdrop lockdrop = new WemixLockdrop(msg.sender);
            user.lockdropInstance = lockdrop;
        }

        user.lockdropInstance.lock{value: msg.value}();
        user.depositAmount += msg.value;
        totalDeposit += msg.value;
    }

    function releaseByDev(address _user) external {
        require(msg.sender == dev, "only dev");
        require(
            block.timestamp >= lockdropFinishTime,
            "lockdrop is not finished"
        );

        UserInfo storage user = userInfo[_user];
        require(user.depositAmount > 0, "invalid user");

        // set deposit amount to 0
        uint256 amount = user.depositAmount;
        user.depositAmount = 0;

        // return principal
        user.lockdropInstance.unlock(amount);
    }

    function release() external lock {
        require(
            block.timestamp >= lockdropFinishTime,
            "lockdrop is not finished"
        );

        UserInfo storage user = userInfo[msg.sender];
        require(user.depositAmount > 0, "invalid user");

        // set deposit amount to 0
        uint256 amount = user.depositAmount;
        user.depositAmount = 0;

        // return principal
        user.lockdropInstance.unlock(amount);
    }

    function commitReferralReward(address _user, uint256 _cYield) external {
        require(
            msg.sender == address(userInfo[_user].lockdropInstance),
            "invalid lockdrop instance"
        );
        ReferralPool(referralPool).commitReferralReward(_user, _cYield);
    }

    receive() external payable {}
}

