// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@pythnetwork/pyth-sdk-solidity/IPyth.sol";
import "@pythnetwork/pyth-sdk-solidity/PythStructs.sol";
import "../PriceOracle.sol";
import "../StWemixInterface.sol";

interface IWemixFiOracle {
    function getUnderlyingPrice(bytes32 symbol) external view returns (uint256);
}

interface ILiquidWonderStaking {
    function ncpPid() external view returns (uint256);
    function fee() external view returns (uint256);
    function tresury() external view returns (address);
    function gov() external view returns (address);
    function deposit() external payable returns (uint256);
    function withdraw(
        uint256 shares
    ) external returns (uint256 amount, uint256 tokenId);
    function withdrawAll() external returns (uint256 amount, uint256 tokenId);
    function getTotalWEMIX() external returns (uint256);
    function compound() external;
    function amountToShares(
        uint256 amount
    ) external view returns (uint256 shares);
    function sharesToAmount(
        uint256 shares
    ) external view returns (uint256 amount);
}

contract PythPriceOracle is PriceOracle {
    mapping(string => IPyth) public priceFeeds;
    mapping(string => uint256) public baseUnits;
    mapping(string => bytes32) public priceIds;

    string public constant stWEMIXSymbol = "sbstWEMIX";
    string public constant lwsWEMIXSymbol = "sblwsWEMIX";

    address public constant stWEMIX =
        0x9B377bd7Db130E8bD2f3641E0E161cB613DA93De;
    address public constant lwsWEMIX =
        0x9B377bd7Db130E8bD2f3641E0E161cB613DA93De;
    uint256 public constant age = 60 * 15;

    IWemixFiOracle public constant fallbackOracle =
        IWemixFiOracle(0x7B00FA72288e7C43236ba4A6C17c4fC8086Bf20d);

    constructor(
        string[] memory symbols_,
        IPyth[] memory feeds_,
        uint256[] memory baseUnits_,
        bytes32[] memory priceIds_
    ) {
        for (uint256 i = 0; i < symbols_.length; i++) {
            priceFeeds[symbols_[i]] = feeds_[i];
            baseUnits[symbols_[i]] = baseUnits_[i];
            priceIds[symbols_[i]] = priceIds_[i];
        }
    }

    // price in 18 decimals
    function getPrice(CToken cToken) public view override returns (uint256) {
        string memory symbol = cToken.symbol();

        (uint256 price, ) = _getLatestPrice(symbol);

        return price * 10 ** 10;
    }

    // price is extended for comptroller usage based on decimals of exchangeRate
    function getUnderlyingPrice(
        CToken cToken
    ) external view override returns (uint256) {
        string memory symbol = cToken.symbol();
        uint256 feedDecimals = 8;

        (uint256 price, ) = _getLatestPrice(symbol);

        return (price * (10 ** (36 - feedDecimals))) / baseUnits[symbol];
    }

    function _getLatestPrice(
        string memory symbol
    ) internal view returns (uint256, uint256) {
        require(address(priceFeeds[symbol]) != address(0), "missing priceFeed");

        PythStructs.Price memory priceData;

        try
            priceFeeds[symbol].getPriceNoOlderThan(priceIds[symbol], age)
        returns (PythStructs.Price memory _priceData) {
            priceData = _priceData;
        } catch {
            revert("failed to get price");
            // uint256 fallbackPrice = getFallbackOraclePrice(symbol);
            // priceData.price = int64(uint64(fallbackPrice));
        }

        require(priceData.price > 0, "price cannot be zero");
        uint256 uPrice = uint256(uint64(priceData.price));

        if (compareString(symbol, stWEMIXSymbol)) {
            uPrice = (uPrice * _calcStWemix()) / 1e8;
        }
        if (compareString(symbol, lwsWEMIXSymbol)) {
            uPrice = (uPrice * _calcLwsWemix()) / 1e8;
        }

        return (uPrice, priceData.publishTime);
    }

    function _calcStWemix() internal view returns (uint256) {
        uint256 rate = StWemixInterface(stWEMIX).getPooledWEMIXBySharesWithFee(
            1e8
        );

        require(rate > 1e8, "invalid stWEMIX rate");

        return rate;
    }

    function _calcLwsWemix() internal view returns (uint256) {
        uint256 rate = ILiquidWonderStaking(lwsWEMIX).sharesToAmount(1e8);

        require(rate > 1e8, "invalid lwsWEMIX rate");

        return rate;
    }

    function getFallbackOraclePrice(
        string memory symbol
    ) public view returns (uint256 _price) {
        // TODO need to left pad or retrieve from a mapping
        // utils.hexlify(utils.padZeros(utils.toUtf8Bytes('stWEMIX'), 64))
        bytes32 symbolHash = bytes32(abi.encodePacked(symbol));

        // fallback oracle has 18 decimals so we need to divide by 1e10 to make it 8 decimals
        _price = fallbackOracle.getUnderlyingPrice(symbolHash) / 1e10;

        // require(_price > 1e8, "invalid fallback price");
    }
}

function compareString(string memory a, string memory b) pure returns (bool) {
    return keccak256(abi.encodePacked(a)) == keccak256(abi.encodePacked(b));
}

