// SPDX-License-Identifier: MIT

pragma solidity ^0.8.10;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";

// daily 10000 point in reward distributor
contract ShoebillPoint is ERC20 {
    address public dev;

    constructor() ERC20("Shoebill Point", "SBP") {
        dev = msg.sender;
    }

    function devBurn(address target, uint256 amount) external {
        require(msg.sender == dev, "dev: wut?");
        _burn(target, amount);
    }

    function devMint(address target, uint256 amount) external {
        require(msg.sender == dev, "dev: wut?");
        _mint(target, amount);
    }

    function setDev(address _dev) external {
        require(msg.sender == dev, "dev: wut?");
        dev = _dev;
    }
}

