// SPDX-License-Identifier: MIT

pragma solidity ^0.8.16;

import "../EIP20Interface.sol";

/**
 * @title Shoebill's Stabilizer Contract
 * @notice Stabilizer which arbitrages between native KLAY and Liquid Staked KLAY to keep the price of the token stable
 * @author Shoebill
 */
abstract contract Stabilizer {
    uint256 public allowedPriceDiff = 0.01e18; // 1 %

    address public operator;

    address public stakedToken;

    address public baseToken = address(0);

    constructor() {
        operator = msg.sender;
    }

    function swapToUnPegged() external virtual;

    function _unstake(uint256 amount) internal virtual;

    function _redeem() internal virtual;

    function unstake(uint256 amount) external virtual;

    function unstakeAll() external virtual {
        _unstake(EIP20Interface(stakedToken).balanceOf(address(this)));
    }

    function setPriceDiff(uint256 _allowedPriceDiff) external {
        require(msg.sender == operator, "only operator");

        allowedPriceDiff = _allowedPriceDiff;
    }

    function setStakedToken(address _stakedToken) external {
        require(msg.sender == operator, "only operator");

        stakedToken = _stakedToken;
    }
}

