pragma solidity 0.8.16;

import {ERC20Upgradeable} from "@openzeppelin/contracts-upgradeable/token/ERC20/ERC20Upgradeable.sol";
import {ReentrancyGuardUpgradeable} from "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";

import {IERC20} from "../IERC20.sol";
import {WrapperUpgradeable} from "./WrapperUpgradeable.sol";
import {CTokenInterface} from "../../CTokenInterfaces.sol";

contract RebasedSbWemix is WrapperUpgradeable {
    function initialize() public initializer {
        __ERC20_init_unchained("Rebased Shoebill WEMIX", "rbsbWEMIX");
        __ReentrancyGuard_init_unchained();
        shareToken = 0xD42ad8346d14853EB3D30568B7415cF90C579D83; // sbWEMIX
    }

    /**
     * @notice Returns amount of stAmount for corresponding shares
     * @dev Shares --> Amount
     * @param sharesAmount amount of shares to convert
     */
    function _getAmountByShares(
        uint256 sharesAmount
    ) internal view override returns (uint256) {
        uint256 exchangeRateStored = CTokenInterface(shareToken)
            .exchangeRateStored();
        return (sharesAmount * exchangeRateStored) / 1e18;
    }

    /**
     * @notice Returns amount of shares for corresponding stAmount
     * @dev Amount --> Shares
     * @param tokenAmount amount of stAmount to convert
     */
    function _getSharesByAmount(
        uint256 tokenAmount
    ) internal view override returns (uint256) {
        uint256 exchangeRateStored = CTokenInterface(shareToken)
            .exchangeRateStored();

        return (tokenAmount * 1e18) / exchangeRateStored;
    }

    function _totalSupply() internal view override returns (uint256) {
        return
            _getAmountByShares(IERC20(shareToken).balanceOf((address(this))));
    }
}

