contracts/Swap/Wrapper/WrapperUpgradeabl.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.8.16;

import {ERC20Upgradeable} from "@openzeppelin/contracts-upgradeable/token/ERC20/ERC20Upgradeable.sol";
import {ReentrancyGuardUpgradeable} from "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";

import {IERC20} from "../IERC20.sol";

abstract contract WrapperUpgradeable is
    ERC20Upgradeable,
    ReentrancyGuardUpgradeable
{
    address public constant ZERO_ADDRESS = address(0);

    address public shareToken;

    mapping(address => uint256) public shares; // stwemix amount
    ///////////////////////////////////////////////////////////////////
    //     Events
    ///////////////////////////////////////////////////////////////////

    event SharesChanged(
        address indexed user,
        uint256 prevShares,
        uint256 shares
    );

    ///////////////////////////////////////////////////////////////////
    //     External Functions (View)
    ///////////////////////////////////////////////////////////////////

    /**
     * @notice Returns amount of stAmount for corresponding shares
     * @dev Shares --> Amount
     * @param amount amount of shares to convert
     */
    function getSharesByAmount(uint256 amount) external view returns (uint256) {
        return _getSharesByAmount(amount);
    }

    /**
     * @notice Returns amount of shares for corresponding stAmount
     * @dev Amount --> Shares
     * @param amount amount of stAmount to convert
     */
    function getAmountByShares(uint256 amount) external view returns (uint256) {
        return _getAmountByShares(amount);
    }

    ///////////////////////////////////////////////////////////////////
    //     Public Functions
    ///////////////////////////////////////////////////////////////////

    /**
     * @notice Returns user balance in stAmount amount
     */
    function balanceOf(address user) public view override returns (uint256) {
        return _getAmountByShares(sharesOf(user));
    }

    /**
     * @notice Returns user balance in shares
     */
    function sharesOf(address user) public view returns (uint256) {
        return shares[user];
    }

    /**
     * @notice Returns amount of wemix in.
     */
    function totalSupply() public view override returns (uint256) {
        return _totalSupply();
    }

    function balance() public view returns (uint256) {
        return IERC20(shareToken).balanceOf(address(this));
    }

    function totalShares() public view returns (uint256) {
        return balance();
    }

    ///////////////////////////////////////////////////////////////////
    //     External Functions
    ///////////////////////////////////////////////////////////////////

    /**
     * @notice Deposits for sender
     * Emits a `Transfer` event from zero address to msgSender
     */
    function stake(uint256 _amount) external nonReentrant {
        _safeTransferFrom(shareToken, msg.sender, address(this), _amount);
        _stake(msg.sender, _amount);
    }

    /**
     * @notice Deposits for designated address
     * @param recipient address to deposit for
     * Emits a `Transfer` event from zero address to recipient
     */
    function stakeFor(
        uint256 _amount,
        address recipient
    ) external nonReentrant {
        _safeTransferFrom(shareToken, msg.sender, address(this), _amount);
        _stake(recipient, _amount);
    }

    /**
     * @notice Requests unstaking to sender
     * @param amount amount to unstake
     * Emits a `Transfer` event from msgSender to zero address
     */
    function unstake(uint256 amount) external nonReentrant {
        uint256 stAmount = _unstake(amount);
        _safeTransfer(shareToken, msg.sender, stAmount);
    }

    /**
     * @notice Requests unstaking to sender
     * @param amount amount to unstake
     * Emits a `Transfer` event from msgSender to zero address
     */
    function unstakeShares(uint256 amount) external nonReentrant {
        uint256 stAmount = _unstakeShares(amount);
        _safeTransfer(shareToken, msg.sender, stAmount);
    }

    ///////////////////////////////////////////////////////////////////
    //     Internal Functions
    ///////////////////////////////////////////////////////////////////

    /**
     * @notice Transfers token ownership
     * @param from address of current token owner
     * @param to address of new token owner
     * @param amount amount of tokens to transfer
     * Emits a `Transfer` event
     */
    function _transfer(
        address from,
        address to,
        uint256 amount
    ) internal override {
        _beforeTokenTransfer(from, to, amount);

        _transferShares(from, to, _getSharesByAmount(amount));
        emit Transfer(from, to, amount);

        _afterTokenTransfer(from, to, amount);
    }

    function _safeTransfer(address token, address to, uint256 value) internal {
        require(token.code.length > 0, "SafeERC20: invalid token");
        (bool success, bytes memory data) = token.call(
            abi.encodeWithSelector(IERC20.transfer.selector, to, value)
        );
        require(
            success && (data.length == 0 || abi.decode(data, (bool))),
            "SafeERC20: transfer failed"
        );
    }

    function _safeTransferFrom(
        address token,
        address from,
        address to,
        uint256 value
    ) internal {
        require(token.code.length > 0, "SafeERC20: invalid token");
        (bool success, bytes memory data) = token.call(
            abi.encodeWithSelector(
                IERC20.transferFrom.selector,
                from,
                to,
                value
            )
        );
        require(
            success && (data.length == 0 || abi.decode(data, (bool))),
            "SafeERC20: transfer failed"
        );
    }

    function _stake(address user, uint256 amount) internal nonReentrant {
        uint256 sharesToMint = amount;

        _mintShares(user, sharesToMint);

        emit Transfer(ZERO_ADDRESS, user, _getAmountByShares(sharesToMint));
    }

    function _unstake(uint256 amount) internal nonReentrant returns (uint256) {
        address user = msg.sender;

        uint256 sharesToBurn = _getSharesByAmount(amount);
        _burnShares(user, sharesToBurn);

        emit Transfer(user, ZERO_ADDRESS, amount);
        return sharesToBurn;
    }

    function _unstakeShares(
        uint256 amount
    ) internal nonReentrant returns (uint256) {
        address user = msg.sender;

        uint256 sharesToBurn = amount;
        _burnShares(user, sharesToBurn);

        emit Transfer(user, ZERO_ADDRESS, amount);
        return sharesToBurn;
    }

    /**
     * @notice Mints new shares of the token to a certain user
     * @param user address to mint shares to
     * @param amount amount to mint
     * Emits a `SharesChanged` event
     */
    function _mintShares(address user, uint256 amount) internal {
        uint256 prevShares = sharesOf(user);
        shares[user] = prevShares + amount;

        emit SharesChanged(user, prevShares, shares[user]);
    }

    /**
     * @notice Burns shares of the token
     * @param user address to burn for
     * @param amount amount to burn
     * Emits a `SharesChanged` event
     */
    function _burnShares(address user, uint256 amount) internal {
        uint256 prevShares = sharesOf(user);
        require(prevShares >= amount, "StAmount:: insufficient shares");

        shares[user] = prevShares - amount;

        emit SharesChanged(user, prevShares, shares[user]);
    }

    /**
     * @notice Transfers shares from one address to another
     * @param from address to transfer from
     * @param to address to transfer to
     * @param amount amount to transfer
     * Emits a `SharesChanged` event
     */
    function _transferShares(
        address from,
        address to,
        uint256 amount
    ) internal {
        uint256 fromShares = sharesOf(from);
        require(fromShares >= amount, "StAmount:: insufficient shares");

        shares[from] = fromShares - amount;
        uint256 toShares = sharesOf(to);
        shares[to] = toShares + amount;

        emit SharesChanged(from, fromShares, shares[from]);
        emit SharesChanged(to, toShares, shares[to]);
    }

    /**
     * @notice Returns amount of stAmount for corresponding shares
     * @dev Shares --> Amount
     * @param sharesAmount amount of shares to convert
     */
    function _getAmountByShares(
        uint256 sharesAmount
    ) internal view virtual returns (uint256);

    /**
     * @notice Returns amount of shares for corresponding stAmount
     * @dev Amount --> Shares
     * @param tokenAmount amount of stAmount to convert
     */
    function _getSharesByAmount(
        uint256 tokenAmount
    ) internal view virtual returns (uint256);

    function _totalSupply() internal view virtual returns (uint256);
}

contracts/Swap/WrappingRouter.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.8.16;

import {IERC20} from "./IERC20.sol";
import {WrapperInterface} from "./Wrapper/WrapperInterface.sol";
import {Router} from "./Router.sol";

contract WrappingRouter is Router {
    mapping(address => address) private _tokenWrappers;

    address private _wrapperSetter;

    constructor(address _factory, address _weth) Router(_factory, _weth) {
        _wrapperSetter = msg.sender;
    }

    /// @notice Set Wrapper ERC20 Address
    /// @param _token yield bearing token address e.g. stWEMIX, sbWEMIX, stsbWEMIX
    /// @param _wrapper wrapper ERC20 address e.g. RebasedSbWemix etc..
    function setWrapper(address _token, address _wrapper) external {
        require(msg.sender == _wrapperSetter, "Router: FORBIDDEN");
        _tokenWrappers[_token] = _wrapper;
    }

    function swapExactTokensForTokensSimpleWrapper(
        uint amountIn,
        uint amountOutMin,
        address tokenFrom,
        address tokenTo,
        bool stable,
        address to,
        uint deadline
    ) external ensure(deadline) returns (uint[] memory amounts) {
        Route[] memory routes = new Route[](1);
        routes[0].from = _transformToWrap(tokenFrom);
        routes[0].to = _transformToWrap(tokenTo);
        routes[0].stable = stable;
        amounts = getAmountsOut(amountIn, routes);
        require(
            amounts[amounts.length - 1] >= amountOutMin,
            "Router: INSUFFICIENT_OUTPUT_AMOUNT"
        );
        _wrapAndSendToPair(tokenFrom, amounts[0], routes[0]);

        // TODO: to address(this)
        _swap(amounts, routes, address(this));

        _unwrapAndSend(tokenTo, amounts[amounts.length - 1], to);
    }

    function swapExactETHForTokensSimpleWrapper(
        uint amountOutMin,
        address tokenFrom, // WETH
        address tokenTo,
        bool stable,
        address to,
        uint deadline
    ) external payable ensure(deadline) returns (uint[] memory amounts) {
        require(tokenFrom == address(weth), "Router: INVALID_PATH");

        Route[] memory routes = new Route[](1);
        routes[0].from = _transformToWrap(tokenFrom);
        routes[0].to = _transformToWrap(tokenTo);
        routes[0].stable = stable;
        amounts = getAmountsOut(msg.value, routes);
        require(
            amounts[amounts.length - 1] >= amountOutMin,
            "Router: INSUFFICIENT_OUTPUT_AMOUNT"
        );
        weth.deposit{value: amounts[0]}();

        _wrapAndSendToPair(tokenFrom, amounts[0], routes[0]);

        _swap(amounts, routes, address(this));

        _unwrapAndSend(tokenTo, amounts[amounts.length - 1], to);
    }

    function _transformToWrap(address _token) internal view returns (address) {
        if (_tokenWrappers[_token] == address(0)) return _token;

        return _tokenWrappers[_token];
    }

    function _unwrapAndSend(
        address _token,
        uint256 amount,
        address to
    ) internal {
        address wrapper = _tokenWrappers[_token];

        if (wrapper == address(0)) {
            _safeTransfer(_token, to, amount);
        } else {
            WrapperInterface(wrapper).unstake(amount);
            _safeTransfer(_token, to, IERC20(_token).balanceOf(address(this)));
        }
    }

    function _wrapAndSendToPair(
        address _token,
        uint256 amount,
        Route memory route
    ) internal {
        address wrapper = _tokenWrappers[_token];
        if (wrapper == address(0)) {
            _safeTransferFrom(
                route.from,
                msg.sender,
                pairFor(route.from, route.to, route.stable),
                amount
            );
        } else {
            // transfer token to this contract
            _safeTransferFrom(_token, msg.sender, address(this), amount);

            IERC20(_token).approve(wrapper, amount);
            // wrap
            WrapperInterface(wrapper).stake(amount);

            _safeTransfer(
                wrapper,
                pairFor(route.from, route.to, route.stable),
                IERC20(wrapper).balanceOf(address(this))
            );
        }
    }
}

